--RECEIPT-----------------------------------------------------------------------------------------------------------------------------------------------------------------------
CREATE TABLE RECEIPT(
    NO                         NUMBER       NOT NULL   PRIMARY KEY,
    MEMBER_NO                          NUMBER       NOT NULL,
    RECEIPT_PROCESSING_STATE_NO        NUMBER       NOT NULL,
    CATEGORY_NO                        NUMBER       NOT NULL,
    RECEIPT_SERIAL                      VARCHAR2(50) NOT NULL,
    PAYMENT_DATE                        TIMESTAMP    DEFAULT    SYSDATE,
    VENDOR                              VARCHAR2(50) NOT NULL
);

ALTER TABLE RECEIPT ADD CONSTRAINT FK_RECEIPT_MEMBER FOREIGN KEY(MEMBER_NO) references MEMBER(NO);
ALTER TABLE RECEIPT ADD CONSTRAINT FK_RECEIPT_RECEIPT_PROCESSING_STATE FOREIGN KEY(RECEIPT_PROCESSING_STATE_NO) REFERENCES RECEIPT_PROCESSING_STATE(NO);
ALTER TABLE RECEIPT ADD CONSTRAINT FK_RECEIPT_CATEGORY FOREIGN KEY(CATEGORY_NO) REFERENCES CATEGORY(NO);

