--RECEIPT_PROCESSING_HISTORY------------------------------------------------------------------------------------------------------------------------------------------
CREATE TABLE RECEIPT_PROCESSING_HISTORY (
	NO	    NUMBER		NOT NULL PRIMARY KEY,
	MANAGER_NO	                NUMBER		NOT NULL,
	RECEIPT_NO	                NUMBER		NOT NULL,
	RECEIPT_PROCESSING_STATE_NO	NUMBER	    DEFAULT 1	    NOT NULL,
