COMMENT ON COLUMN RECEIPT_PROCESSING_HISTORY.NOTE IS '비고';

ALTER TABLE RECEIPT_PROCESSING_HISTORY ADD CONSTRAINT FK_RECEIPT_PROCESSING_HISTORY_MANAGER FOREIGN KEY(MANAGER_NO) references MANAGER(NO);
ALTER TABLE RECEIPT_PROCESSING_HISTORY ADD CONSTRAINT FK_RECEIPT_PROCESSING_HISTORY_RECEIPT FOREIGN KEY(RECEIPT_NO) references RECEIPT(NO);
ALTER TABLE RECEIPT_PROCESSING_HISTORY ADD CONSTRAINT FK_RECEIPT_PROCESSING_HISTORY_RECEIPT_PROCESSING_STATE FOREIGN KEY(RECEIPT_PROCESSING_STATE_NO) references RECEIPT_PROCESSING_STATE(NO);

